mapper.insert:
INSERT INTO TB_COMMUNITY_REPLY (
    SEQ
    , REPLY_TEXT
    , REPLY_WRITER
) VALUES (
    #{seq}
    , #{replyText}
    , #{replyWriter}
)

        INSERT INTO TB_COMMUNITY_REPLY
		(
			SEQ,
			REPLY_TEXT,
			REPLY_WRITER,
			REG_DATE,
			UPDATE_DATE,
			REPARENT,
			REORDER
		)
		VALUES
		(
			#{seq}
			, #{replyText}
			, #{replyWriter}
			, NOW()
			, NOW()
			, #{reparent}
			, GEN_COMMUNITY_REPLY_ORDER(#{seq},#{reparent})
		)

mapper.update:
UPDATE 
	TB_COMMUNITY_REPLY
SET 
	REPLY_TEXT = #{replyText}
	,UPDATE_DATE = NOW()
WHERE REPLY_NO = #{replyNo}

mapper.select:
SELECT A.REPLY_NO AS PreplyNo , A.SEQ AS Pseq , A.REPLY_TEXT AS PreplyText , A.REPLY_WRITER AS PreplyWriter , A.REG_DATE AS PregDate , A.UPDATE_DATE AS PupdateDate
, B.REPLY_NO AS CreplyNo , B.SEQ AS Cseq , B.REPLY_TEXT AS CreplyText , B.REPLY_WRITER AS CreplyWriter , B.REG_DATE AS CregDate , B.UPDATE_DATE AS CupdateDate , B.REORDER AS reorder FROM ( SELECT @rownum:=@rownum+1 NO, A.* FROM TB_COMMUNITY_REPLY A, (SELECT @rownum:=0) R WHERE A.SEQ = #{seq} AND A.REPARENT = 0 ) A LEFT JOIN TB_COMMUNITY_REPLY B ON B.SEQ = A.SEQ AND B.REPARENT = A.REPLY_NO
ORDER BY A.NO DESC , B.REORDER LIMIT #{criteria.pageStart}, #{criteria.perPageNum}
